import{LightningElement}from"lwc";import fetchContactDetails from"@salesforce/apex/DESE_MyProfileController.getContactDetails";import updateContactDetails from"@salesforce/apex/DESE_MyProfileController.updateContactDetails";import{ShowToastEvent}from"lightning/platformShowToastEvent";import deseDesign from"@salesforce/resourceUrl/DESE_Design";export default class Dese_my_profile extends LightningElement{profileImage=deseDesign+"/theme/images/Profile.png";contact={};connectedCallback(){fetchContactDetails().then((t=>{this.contact=JSON.parse(t),console.log(JSON.stringify(this.contact))})).catch((t=>{console.log(JSON.stringify(t))}))}handleFirstNameChange(t){this.contact.FirstName=t.detail.value}handleLastNameChange(t){this.contact.LastName=t.detail.value}handleEmailChange(t){this.contact.Email=t.detail.value}handlePhoneChange(t){this.contact.Phone=t.detail.value}addressInputChange(t){contact.MailingStreet=t.target.street,contact.MailingCity=t.target.city,contact.MailingState=t.target.province,contact.MailingCountry=t.target.country,contact.MailingPostalCode=t.target.postalCode}handleCancel(){window.open("/dese/s/","_self")}handleSave(){updateContactDetails({contactJSON:JSON.stringify(this.contact)}).then((t=>{if("SUCCESS"==t){const t=new ShowToastEvent({title:"Success!!",message:"Details Updated Successfully !!",variant:"success",mode:"dismissable"});this.dispatchEvent(t)}})).catch((t=>{console.log(JSON.stringify(t))}))}}